mapper.select:
select

from factory_info
 1=1

	and name like '%${t.name}%'


	and id = ${t.id}


	and pinyin = ${t.pinyin}


	and mark like '%${t.mark}%'

	and city_id =${t.cityId}

	and status =${t.status}

	and isdefault ='${t.isdefault}'


order by status,id desc

select
from factory_info 1=1
and name like '%${t.name}%' or pinyin like '%${t.name}%'
and city_Id = #{t.cityId}
and status = #{t.status}
order by id desc

mapper.insert:
insert into factory_info (
	name, pinyin, mark,city_id,status,isdefault
)
values(
	#{name},
	#{pinyin},
	#{mark},
	#{cityId},
	#{status},#{isdefault}
)
