mapper.select:
select

	from order_input_additional oa,order_input oi,cloth_info cu,salesman_info si 

oa.input_id=oi.id and oi.cloth_id=cu.id and si.id=oi.salesman_id

	and cloth_id = #{t.clothId}


	and my_company_code = #{t.myCompanyCode}


	and salesman_id = #{t.salesmanId}


	and status = #{t.status}


	and oa.mark  like '%${t.mark}%'


order by id desc

mapper.insert:
insert into order_input (
	cloth_id, my_company_code, salesman_id, status, create_time
)
values(
	#{clothId},
	#{myCompanyCode},
	#{salesmanId},
	#{status},
	#{createTime}
)
