mapper.select:
select

	from order_input


	and cloth_id = #{t.clothId}


	and my_company_code = #{t.myCompanyCode}


	and salesman_id = #{t.salesmanId}


	and status = #{t.status}


order by id desc

mapper.insert:
insert into order_input (
	cloth_id, my_company_code, salesman_id, status, create_time
)
values(
	#{clothId},
	#{myCompanyCode},
	#{salemanId},
	#{status},
	#{createTime}
)
