mapper.select:
select
from order_input_additional 1=1
and input_id = #{t.inputId}
and my_company_code = #{t.myCompanyCode}
mark like '%${t.mark}%'
order by id desc

mapper.insert:
insert into order_input_additional (
	input_id, my_company_color, num, unit, mark
)
values(
	#{inputId},
	#{myCompanyCode},
	#{num},
	#{unit},
	#{mark}
)
