mapper.select:
select

	from order_input_summary
 1=1

	and cloth_id = #{t.clothId}


	and my_company_code = #{t.myCompanyCode}


	and my_company_color = #{t.myCompanyColor}


	and num = #{t.num}


		mark like '%${t.mark}%'


		order_ids like '%${t.orderIds}%'


order by id desc

