select
from order_summary 1=1
and print_num = 0
and order_date >= #{beginTime}
and order_date <= #{endTime}
order by id desc

select orde.id, order_code orderCode, order_date orderDate,cloth_id clothId, color, (select cloth_name from cloth_info where id=cloth_id) as clothName, CAST((select tiao_kg from cloth_info where id=cloth_id)*num AS CHAR) numKg, factory_id factoryId,factory.name factoryName,technology_id technologyId, (select name from technology_info where id=technology_id ) technologyName, my_company_code myCompanyCode,my_company_color myCompanyColor, factory_code factoryCode,factory_color factoryColor, cloth_num clothNum,num,unit,order_Ids orderIds, standard, packing_style packingStyle,print_status printStatus,print_num printNum, return_status returnStatus,orde.status, create_time createTime,orde.mark, CONCAT(salesmans,',',coalesce(balance_saleman_id,'0')) salesmans, zhiguan, kongcha, jiaodai, balance_saleman_id balanceSalemanId, balance, balancemark balancemark,kuanfu, kuanfufs, kezhong, kezhongUnit, kezhongfs,notify_id notifyId,notify_time notifyTime,no from order_summary orde,factory_info factory orde.factory_id=factory.id
and print_status = #{printStatus}
and notify_time >= #{beginTime}
and notify_time <= #{endTime}
and cloth_id = #{clothId}
and city_id = #{cityId}
and factory_id = #{factoryId}
and technology_id = #{technologyId}
and factory_code like '%${factoryCode}%'
and factory_color like '%${factoryColor}%'
and notify_id #{notifyId}
GROUP BY notify_id order by notify_id DESC

select

	from order_summary
 1=1

	and order_code >= #{t.startDate}


	and order_code <= #{t.endDate}


	and cloth_id = #{t.clothId}


	and factory_id = #{t.factoryId}


	and technology_id = #{t.technologyId}


	and my_company_code = #{t.myCompanyCode}


	and (my_company_code like '%${t.code}%' or factory_code like '%${t.code}%'  )


	and my_company_color = #{t.myCompanyColor}


	and factory_code = #{t.factoryCode}


	and factory_color = #{t.factoryColor}


	and cloth_num = #{t.clothNum}


	and standard = #{t.standard}


	and	packing_style like '%${t.packingStyle}%'


	and print_status = #{t.printStatus}


	and print_num = #{t.printNum}


	and status = #{t.status}


	and return_status = #{t.returnStatus}


	and mark like '%${t.mark}%'


		and num  >=#{t.num}


		and num <=#{t.num}


	and datediff(NOW(),order_code) >#{t.delayDates}


order by id desc

select

	from order_summary
 1=1

	and order_code >= #{t.startDate}


	and order_code <= #{t.endDate}


	and cloth_id = #{t.clothId}


	and factory_id = #{t.factoryId}


	and technology_id = #{t.technologyId}


	and my_company_code = #{t.myCompanyCode}


	and (my_company_code like '%${t.code}%' or factory_code like '%${t.code}%'  )


	and my_company_color = #{t.myCompanyColor}


	and factory_code = #{t.factoryCode}


	and factory_color = #{t.factoryColor}


	and cloth_num = #{t.clothNum}


	and standard = #{t.standard}


	and	packing_style like '%${t.packingStyle}%'


	and print_status = #{t.printStatus}


	and print_num = #{t.printNum}


	and status = #{t.status}



		and (return_status=0 or return_status=1)

		and return_status = #{t.returnStatus} 



	and mark like '%${t.mark}%'

	and return_mark like '%${t.returnMark}%'


		and num  >=#{t.num}


		and num <=#{t.num}


	and datediff(NOW(),order_code) >#{t.delayDates}


order by create_time desc,return_status, id desc

