mapper.select:
  	select

		from sample_input


				and sample_date > #{beginTime}


				and sample_date < #{endTime}

select

from sample_input s,cloth_info c,factory_info f,technology_info t

s.cloth_id=c.id and s.factory_id=f.id and s.technology_id=t.id 

	and factory_id = #{t.factoryId}


	and cloth_id = #{t.clothId}


	and s.status = #{t.status}


	and factory_code = #{t.factoryCode}


	and my_company_code = #{t.myCompanyCode}

	and technology_id = #{t.technologyId}


	and s.mark like '%${t.mark}%'


	and s.code_value like '%${t.codeValue}%'

	and s.sample_date >= #{t.startDate}

	and s.sample_date <= #{t.endDate}


	and datediff(NOW(),sample_date) >#{t.delayDates}



order by id desc

select

from sample_input s,cloth_info c,factory_info f,technology_info t

s.cloth_id=c.id and s.factory_id=f.id and s.technology_id=t.id 

	and factory_id = #{t.factoryId}


	and cloth_id = #{t.clothId}


	and factory_code = #{t.factoryCode}


	and my_company_code = #{t.myCompanyCode}

	and technology_id = #{t.technologyId}


	and s.mark like '%${t.mark}%'

	and s.code_value like '%${t.codeValue}%'


	and s.sample_date >= #{t.startDate}

	and s.sample_date <= #{t.endDate}


	and s.reply_date >= #{t.rstartDate}

	and s.reply_date <= #{t.rendDate}


	and s.status = #{t.status}


	and s.id = #{t.id}


order by status,id desc
