mapper.select:
select

from audit
 1=1

	and type = #{type}


	and status = #{status}


	and orderId  = #{orderId }

select

from audit
 1=1

	and type = #{t.type}


	and status = #{t.status}


	and orderId  = #{t.orderId }


order by status,orderTime

mapper.update:
update audit

	
		reason=#{reason},
	
	
		createTime=#{createTime},
	
	
		status=#{status}
	

where id=#{id}

mapper.select:
select

from account where
accountName=#{accountName} and password=#{password}
